import {React, useState} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { addItem, removeItem,selectorTotalCost} from './cartSlice';
import {selectorProducts,selectProductById} from '../products/productsSlice'

export const Cart=()=>{
    // const valoreCarrello=useSelector(selectorCartItems);
    const allProducts=useSelector(selectorProducts);
    const [inputText,setInputText]=useState("");
    const handleChange=(event)=>{setInputText(event.target.value)};
    const dispatch=useDispatch();
    const listItems= allProducts.map((item)=> <li key={item.id} onClick={()=>dispatch(addItem({id:item.id}))}>
        {item.name} - {item.basePrice} - {item.category}</li>);
    const totalCost= useSelector(selectorTotalCost);
    return(
        <>
        {/* <input type="text" id="testo" onChange={handleChange}></input>
        <button onClick={()=>dispatch(addItem({color:inputText,price:12}))}>aggiungi oggetto giallo</button> */}

        <button onClick={()=>dispatch(removeItem({id:0}))}>rimuovi oggetto giallo</button>
        <div className="App">
            <ul>{listItems.length>0? listItems : "carrello vuoto"}</ul>
            <div>{totalCost}</div>
        </div>
        </>
    ); 
}
