import { createSlice } from "@reduxjs/toolkit";

const initialState={
    cartItems: [],
    totalCost: 0
};

const cartSlice=createSlice({
    name: 'cart',
    initialState,
    reducers: {
        addItem:(state,action)=>{
            const index=state.cartItems.findIndex(object=>{return object.id===action.payload.id;});
            if(index===-1)
                state.cartItems.push({
                id:action.payload.id,
                quantity:1
                });
            else
                state.cartItems[index].quantity+=1;

            state.totalCost+=action.payload.basePrice;
    },
    removeItem: (state,action)=>{
        const indexOfItem=state.cartItems.findIndex((item)=> item.id===action.payload.id);
        if(state.cartItems[indexOfItem].quantity==1)
            state.cartItems.splice(indexOfItem);
        state.totalCost-=action.payload.price;
    },
    // removeAllItems: (state)=>{
            
    // },
    }
});
export const {addItem,removeItem}=cartSlice.actions;
export const selectorCartItems=(state)=>state.cart.cartItems;
export const selectorTotalCost=(state)=>state.cart.totalCost;
export default cartSlice.reducer;
