import { createSlice } from "@reduxjs/toolkit";
import Product from "../../class/product";

const product0=new Product(0,"gelato",2,"surgelati")
const product1=new Product(1,"caffe",1,"polvere")
const product2=new Product(2,"patatine",3,"snak")
const initialState={
    // products: [product0,product1,product2],
    products: [
        {id:0,name:"caffe",basePrice: 1,category:"polvere"},
        {id:0,name:"caffe",basePrice: 1,category:"polvere"},
        {id:0,name:"caffe",basePrice: 1,category:"polvere"},
    ],
    countID: 3
};

const productsSlice=createSlice({
    name: 'products',
    initialState,
    reducers: {
        addProduct:(state,action)=>{
            const newProduct=new Product(
                state.countID,
                action.payload.name,
                action.payload.basePrice,
                action.payload.category
                );
            state.products.push(newProduct);
            state.countID+=1;
        }
    }
});
export const {addProduct}=productsSlice.actions;
export const selectorProducts=(state)=>state.products.products;
export default productsSlice.reducer;
