import {React} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { addItem, removeItem,selectorTotalCost} from './cartSlice';
import {selectorProducts} from '../products/productsSlice'

export const Cart=()=>{
    const allProducts=useSelector(selectorProducts);
    const dispatch=useDispatch();
    const listItems= allProducts.map((item)=> <li key={item.id} onClick={()=>dispatch(addItem({id:item.id,basePrice: item.basePrice}))}>
        {item.name}</li>);
    const totalCost= useSelector(selectorTotalCost);
    return(
        <>
        <button onClick={()=>dispatch(removeItem({id:0}))}>rimuovi oggetto giallo</button>
        <div className="App">
            <ul>{listItems.length>0? listItems : "carrello vuoto"}</ul>
            <div>Oggetti pre</div>
            <ul></ul>
            <div>{totalCost}</div> 
        </div>
        </>
    ); 
}
