import {React} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { addItem,selectorCartItems,selectorTotalCost} from './cartSlice';
import {selectorProducts} from '../products/productsSlice'
import CartItem from './CartItem'


export const Cart=()=>{
    const dispatch=useDispatch();
    const allProducts=useSelector(selectorProducts);
    const allCartItems=useSelector(selectorCartItems);
    const listCartItems=[];
    const detailsCartItems=[];
//lista di tutti i prodotti disponibili
    const listLProducts= allProducts.map((item)=> <li key={item.id} onClick={()=>dispatch(addItem({id:item.id,basePrice: item.basePrice}))}>
        {item.name}</li>);
        
    const getDetailsCartItems=(item)=>{
        const detailsItem=allProducts.find((product)=>product.id==item.id);
        if(typeof detailsItem !== "undefined"){
            let dettagli=
            detailsCartItems.push(detailsItem);
        }
    };
    //per ogni elemento presente all'interno del carrello ricavo i dettagli
    allCartItems.forEach(getDetailsCartItems);
    //per ogni elemento presente all'interno del carrello creo il componente react
    detailsCartItems.map((item,index)=>{ 
        listCartItems.push(<CartItem key={index} id={item.id} quantity={item.quantity} name={item.name} price={item.basePrice} />);
    });
    //ricavo il costo totale del carrello
    const totalCost= useSelector(selectorTotalCost);
    return(
        <>
        <div className="App">
            <div>Oggetti disponibili:</div>
            <ul>{listLProducts.length>0? listLProducts : "Non ci sono prodotti disponibili"}</ul>
            <div>Oggetti presenti nel carrello:</div>
            <ul>{listCartItems.length>0? listCartItems : "Il carrello e' vuoto"}</ul>
            <div>{totalCost}</div> 
        </div>
        </>
    ); 
}
